import argparse
import sys
import json
import re
import datetime
import os
import math, csv
from   os import  path
import operator

from   Bio import SeqIO

import BioExt

from Bio.Seq import Seq
from Bio.SeqRecord import SeqRecord
from Bio.Alphabet import IUPAC
from BioExt.uds import _align_par
from BioExt.scorematrices import (
    DNAScoreMatrix,
    FrequenciesError,
    ProteinScoreMatrix
    )

arguments = argparse.ArgumentParser(description='Combine alignments into a single file, adding a reference sequence as well')

arguments.add_argument('-d', '--dir',        help = 'Directory with files',    required = True, type = str )
arguments.add_argument('-o', '--output',       help = 'Write the file to',         required = True, type = argparse.FileType('w'))

genes = ['leader','nsp2','nsp3','nsp4','3C','nsp6','nsp7','nsp8','nsp9','nsp10','RdRp','helicase','exonuclease','endornase','methyltransferase','S','ORF3a','E','M','ORF6','ORF7a','ORF8','N','ORF10']

import_settings = arguments.parse_args()

score_matrix_  = BioExt.scorematrices.DNA95.load()
combined_fasta = {}


ref_genes       = [
    ['genome', '']
]

'''
#compute alignment consensus
'''

def consensus_filter (res):
    if res == '-':
        return 'N'
    return res

consensus = []
ci = 0

for i, gene in enumerate (genes):
    local_set = {}
    
    with open ("".join ([import_settings.dir, ".%s.duplicates.json" % gene]), "r") as dh:
        dups = json.load (dh)
        count = 0
        
        
        for seq_record in SeqIO.parse(open ("".join ([import_settings.dir, ".%s.compressed.fas" % gene]), "r"), "fasta"):
            seq_id   = seq_record.name
            seq = str (seq_record.seq).upper()
            no_count = '_'.join (seq_id.split ('_')[0:-1])
            local_set [no_count] = seq
            _copy_count = len (dups[seq_id])
            count += len (dups[seq_id])
            for i,dup_id in  dups[seq_id].items():
                if dup_id != seq_id:
                    local_set [dup_id] = seq       
        
            if ci == len (consensus):
                for l in enumerate (seq):
                    consensus.append ({})
                #consensus = [{} for l in enumerate (seq)]
                
            for i,l in enumerate (seq):
                if l not in consensus[ci+i]:
                    consensus[ci+i][l] = _copy_count
                else:
                    consensus[ci+i][l] += _copy_count
                
        ci = len (consensus)
        
        
        '''
        '''
 
        if len (combined_fasta):
            to_delete = set ()
            for i in combined_fasta:
                if i in local_set:
                    combined_fasta[i] += local_set[i]
                else:
                    to_delete.add (i)
                
            for i in to_delete:
                del combined_fasta[i]
                
        else:
            for i,s in local_set.items():
                combined_fasta[i] = s

        print (len (combined_fasta), count)
 
ref_seq = ''.join ([consensus_filter(max(pos.items(), key=operator.itemgetter(1))[0]) for pos in consensus ])

#print (ref_seq)

aligned_str = None
def output_record (x):
    global aligned_str 
    l = list(x)
    if len (l) == 1:
        aligned_str = l[0]

def ignore_record (x):
    print (x)
    pass


s = ref_genes[0]
#_align_par (SeqRecord(Seq(s[1],IUPAC.IUPACAmbiguousDNA()),id=s[0]),[SeqRecord(Seq(ref_seq,IUPAC.IUPACAmbiguousDNA()),id="ref")],
_align_par (SeqRecord(Seq(ref_seq,IUPAC.IUPACAmbiguousDNA()),id="ref"),[SeqRecord(Seq(s[1],IUPAC.IUPACAmbiguousDNA()),id=s[0])],
            score_matrix_,False,False,0.1, ignore_record, output_record)
        

ref_map = str(aligned_str.seq.strip('-')).replace ('N','-').replace ('n','-')
insert_gap = []

for k in ref_map:
    if k != k.upper():
        insert_gap.append (True)
    else:
        insert_gap.append (False)
        

 
print (">%s\n%s\n" % ("REFERENCE",ref_map), file = import_settings.output)
            
for i, s in combined_fasta.items():
    print (">%s\n" % (i), file = import_settings.output)
    seq = []
    si = 0
    for k in insert_gap:
        if k:
            seq.append ('-')
        else:
            seq.append (s[si])
            si += 1
    print ("%s\n" % (''.join (seq)), file = import_settings.output)
